import React from 'react';

class Hero extends React.Component {
    state = {
        liKey: 1,
        error: null,
        isLoaded: false,
        heroes: []
    };

    render() {
        const { error, isLoaded, heroes } = this.state;

        if (error) {
            return <div>Error: {error.message}</div>;
        } else if (!isLoaded) {
            return <div>Loading...</div>;
        } else {
            return (
                <React.Fragment>
                    {heroes.map((hero) => (
                        <div className="card" style={{ width: '18rem' }}>
                            <img src={hero.imageUrl} className="card-img-top" alt={hero._id}/>
                            <div className="card-body">
                                <h5 className="card-title">{hero.name} ({hero.likeCount})</h5>
                                <p className="card-text">Hero ID: {hero._id}</p>
                                <ul>
                                    {hero.superPowers.map((superPower) => (
                                        <li key={hero._id+superPower}>
                                            {superPower}
                                        </li>
                                    ))}
                                </ul>
                                <a href={hero.imageUrl} target="_blank" rel="noopener noreferrer"
                                   className="btn btn-primary">Show more</a>
                            </div>
                        </div>
                    ))}
                </React.Fragment>
            );
        }
    }

    // isHero() {
    //     return this.state.heroId < 1000 ? 'A Avenger' : 'Not an Avenger';
    // }

    componentDidMount() {
        fetch('http://localhost:3000/api/heroes')
            .then(res => res.json())
            .then((data) => {
                this.setState({
                    isLoaded: true,
                    heroes: data
                })
            }, (error) => {
                this.setState({
                    isLoaded: false,
                    error
                })
            }).catch((error) => {
               console.log(error.message);
        })
    }
}

export default Hero;
